<template><div><h3 class='text-xl font-semibold mb-4'>Analysis of Data</h3><div class='grid grid-cols-1 md:grid-cols-3 gap-4'><div class='border rounded p-4'><div class='text-sm text-gray-600'>Contracts (External)</div><div class='text-2xl font-bold'>{{ stats.external }}</div></div><div class='border rounded p-4'><div class='text-sm text-gray-600'>Contracts (Internal)</div><div class='text-2xl font-bold'>{{ stats.internal }}</div></div><div class='border rounded p-4'><div class='text-sm text-gray-600'>Favorites</div><div class='text-2xl font-bold'>{{ stats.favorites }}</div></div></div></div></template><script setup>import { ref, onMounted } from 'vue'; import api from '../../api'; const stats=ref({external:0, internal:0, favorites:0}); async function load(){ const { data }=await api.get('/api/contracts/stats'); stats.value=data } onMounted(load)</script>