<template><div><h3 class='text-xl font-semibold mb-4'>Finance</h3><div class='grid grid-cols-1 md:grid-cols-3 gap-4'><div class='border rounded p-4'><div class='text-sm text-gray-600'>Budget Total</div><div class='text-2xl font-bold'>{{ stats.budget_total.toFixed(2) }} USD</div></div><div class='border rounded p-4'><div class='text-sm text-gray-600'>Paid Total</div><div class='text-2xl font-bold'>{{ stats.paid_total.toFixed(2) }} USD</div></div><div class='border rounded p-4'><div class='text-sm text-gray-600'>Overdue Invoices</div><div class='text-2xl font-bold'>{{ stats.overdue_count }}</div></div></div></div></template><script setup>import { ref, onMounted } from 'vue'; import api from '../../api'; const stats=ref({budget_total:0, paid_total:0, overdue_count:0}); async function load(){ const { data }=await api.get('/api/finance/stats'); stats.value=data } onMounted(load)</script>