<template><div><h3 class='text-xl font-semibold mb-4'>Pricing Tool â€” Personal Estimations</h3><form @submit.prevent='calc' class='grid grid-cols-1 md:grid-cols-4 gap-3 max-w-4xl'><div><label class='block text-sm'>Hours</label><input v-model.number='hours' type='number' min='0' step='0.5' class='border rounded p-2 w-full'></div><div><label class='block text-sm'>Rate (USD/h)</label><input v-model.number='rate' type='number' min='0' step='1' class='border rounded p-2 w-full'></div><div><label class='block text-sm'>Materials (USD)</label><input v-model.number='materials' type='number' min='0' step='1' class='border rounded p-2 w-full'></div><div><label class='block text-sm'>Margin (%)</label><input v-model.number='margin' type='number' min='0' max='100' step='1' class='border rounded p-2 w-full'></div><div class='md:col-span-4'><button class='border rounded p-2'>Estimate</button></div></form><div v-if='total>0' class='mt-4 border rounded p-4'>Estimated fee: <strong>{{ total.toFixed(2) }} USD</strong></div></div></template><script setup>import { ref } from 'vue'; const hours=ref(40), rate=ref(50), materials=ref(0), margin=ref(10), total=ref(0); function calc(){ const base=(hours.value*rate.value)+materials.value; total.value= base*(1+margin.value/100) }</script>