<template><div><h3 class='text-xl font-semibold mb-4'>Safety</h3><div class='border rounded p-4'><div>Safety Reports: <strong>{{ stats.safety_reports }}</strong></div><div>Environment Reports: <strong>{{ stats.environment_reports }}</strong></div></div></div></template><script setup>import { ref, onMounted } from 'vue'; import api from '../../api'; const stats=ref({safety_reports:0, environment_reports:0}); async function load(){ const { data }=await api.get('/api/safety/stats'); stats.value=data } onMounted(load)</script>